button.onclick:
//$debug
// build filter to launch the search
var /*String*/ filter = "";
var /*int*/ terms = 0;

// read givenName field
var /*String*/ givenName = meibo.getFieldValue('givenName');
if (givenName.length != 0) {
  terms++;

  filter = filter + "(givenName=" + givenName + "*)";

}

// read sn field
var /*String*/ sn = meibo.getFieldValue('sn');
if (sn.length != 0) {
  terms++;

  filter = filter + "(sn=" + sn + "*)";

}

// read motsCle field
var /*String*/ motsCle = meibo.getFieldValue('motsCle');
if (motsCle.length != 0) {
  terms++;

  filter = filter + "(motsCle=" + motsCle + "*)";

}

// read centreInteret field
var /*String*/ centreInteret = meibo.getFieldValue('centreInteret');
if (centreInteret.length != 0) {
  terms++;

  filter = filter + "(centreInteret=" + centreInteret + "*)";

}

var /*String*/ site = meibo.getFieldValue('site');
if (site.length != 0) {
  terms++;

  filter = filter + "(l=" + site + "*)";

}

var /*String*/ dnProjet = meibo.getFieldValue('projet');
if (dnProjet.length != 0) {
  terms++;
}


// check if there is at least one criteria
//if   (terms == 0) {
//  meibo.showField("errorcriteria", true);
//  meibo.showField("error", false);
//}
//else {
  meibo.showField("errorcriteria", false);


  // add & operator if there are 2 terms or more
  if  ( ( (terms > 1) && (dnProjet == "") ) || ( (terms > 2) && (dnProjet != "") ) )  {
    filter = "(&" +filter + ")";
  }

  if ( terms == 0 ){
    filter = "(CN=*)"
  }

  // launch a first search to know the number of results
  meibo.setFieldValue("filter", filter);
  var /*ResultSet*/ resultSet = meibo.executeResource('People', filter, 0, '');

  var /*int*/ nEntries = resultSet.getEntryCount();

  if (nEntries != 0) {

    var /*String*/ dnProjet = meibo.getFieldValue('projet');
    var /*String*/ dnLabo = meibo.getFieldValue('labo');
    //TODO LATER : filter labo with project
    if (dnLabo.length != 0) {
      meibo.setGlobalVar("RECHERCHE_PROJECT_TROMBINOSCOPE",dnLabo);
    }
    else{
    if (dnProjet.length != 0) {
      meibo.setGlobalVar("RECHERCHE_PROJECT_TROMBINOSCOPE",dnProjet);
    }
    else{
      meibo.setGlobalVar("RECHERCHE_PROJECT_TROMBINOSCOPE","");
    }
    }



    // display table and prepare page number information
    //meibo.showField("searchContainer", true);
    meibo.showField("error", false);


    // launch the search and display results on the same page
     if (dnLabo.length != 0) {
         displayLaboSearch('People', meibo.getFieldValue("attributeList"));
         }
         else {
    displaySearch('People', meibo.getFieldValue("attributeList"));
    }
  }
  else {

    // no result found
    //meibo.showField("searchContainer", false);
    var /*String*/ msg = null;
    try {
      msg = meibo.getMessage("noResultFound");
    }
    catch (ex) {
    }
    if (msg == null) {
      if (meibo.getUserLanguage() == "fr") {
        msg = "Aucun résultat trouvé. Essayez avec d'autres critères";
      }
      else {
        msg = "No result found. Change search criterias";
      }
    }
    meibo.setFieldValue("error", msg);
    meibo.showField("error", true);
  }
//}
