button.onclick:
clearTable("PersonnesACT");
// Get valeurs

  var /*Date*/ Debut = meibo.getFieldValue('DateDebut');
  var /*Date*/ Fin = meibo.getFieldValue('DateFin');


 var tt = SQL_DATE_FORMAT.format(DATE_FORMAT.parse(Debut));


  // Filters

  var /*String*/ filter = "";

  if( Debut != "" ) {
      if( filter != "" ) { 
           filter = filter + " AND ";
       }
      filter = filter + "RAS_START_DATE >= '" + SQL_DATE_FORMAT.format(DATE_FORMAT.parse(Debut)) + "'";
     }

    if( Fin != "" ) {
         if( filter != "" ) { 
              filter = filter + " AND "; 
            }
         filter = filter + "RAS_END_DATE <= '" + SQL_DATE_FORMAT.format(DATE_FORMAT.parse(Fin)) + "'";
    }

    meibo.getField("filters").setValue(filter); 

  //Recherche et afichage table 1

  var missionTable = meibo.getField("PersonnesACT");
  var /*int*/ nbMissions = missionTable.fillInWithQuery('MissionList', filter, 0);


var datesp;
  var datespp;  
  var d;
  var d1;
  var DATE_FORMAT = new java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss");





for (var row = 1; row <nbMissions-1; row++) {

      datesp=missionTable.getCell(row,COL_Dates);
      datespp=missionTable.getCell(row+1,COL_Dates);

      d=SQL_DATE_FORMAT.format(DATE_FORMAT.parse(datesp));
      d1=SQL_DATE_FORMAT.format(DATE_FORMAT.parse(datespp));

      if(missionTable.getCell(row,COL_MissionId) == missionTable.getCell(row+1,COL_MissionId)){
          if(d < d1){
             missionTable.deleteRow(row+1);
             nbMissions=nbMissions-1;
             row=row-1;
           }
          else{ 
             missionTable.deleteRow(row);
             nbMissions=nbMissions-1;
             row=row-1;
           }
       }
  }





  if( nbMissions == 0 ) {
    meibo.getField("nmissions").setValue("Aucune mission ne correspond aux critères");
    meibo.showField("PersonnesACT",false);
  }
  else {
    if( nbMissions == 1 ) {
      meibo.getField("nmissions").setValue("1 mission correspond aux critères");
    }
    else {
      meibo.getField("nmissions").setValue(nbMissions + " missions correspondent aux critères");
    }
    meibo.showField("PersonnesACT",true);
  }

    var /*String*/ ownerId = "";
  var /*String*/ owner = "";
  var /*String*/    missionDn = null;
  var /*resultSet*/ missionEventRS = null;
  for (var row = 0; row < nbMissions; row++) {
    // On récupère la description du propriétaire de la mission
    // et on va mettre à jour les colonnes contenant le nom/prénom
    ownerId = missionTable.getCell(row,COL_OwnerName)
    owner = meibo.getDnEntry("TravelPeople","T_PERSON_PRS,PRS_ID," + ownerId, "PRS_NAME,PRS_SIRNAME");
    missionTable.addCell(row,COL_OwnerName,    owner.PRS_NAME[0]);
    missionTable.addCell(row,COL_OwnerSurname, owner.PRS_SIRNAME[0]);

    // On va rechercher le premier évènement de la mission
    // et mettre à jour la colonne contenant le premier évènement dans le tableau
    missionDn = "T_MISSION_TMN,TMN_ID," + missionTable.getCell(row,COL_MissionId);
    missionEventRS = meibo.executeLink("MissionEvent", missionDn,"TET_NAME,TCT_START_DATE,TCT_DURATION");
    //missionTable.addRawCell(row,COL_MissionId,"<span title=\"blabla\">" + missionTable.getCell(row,COL_MissionId) + "</span>");
    missionTable.addCell(row,COL_FirstEvent,missionEventRS.getEntry().TET_NAME[0]);
  }

  // La description d'une mission ne doit pas être affiché par défaut
  //meibo.showField("OBJ_descriptionMission",false);

multilist.ondoubleclick:
///$debug

// on récupère le DN de la mission
var /*area*/   missionTable = meibo.getCurrentField();
var /*int*/    row = missionTable.selectedRow;
var /*string*/ missionDn = "T_MISSION_TMN,TMN_ID," + missionTable.getCell(row,COL_MissionId);

// on affiche le participant à la mission
var name    = missionTable.getCell(row,COL_OwnerName);
var surname = missionTable.getCell(row,COL_OwnerSurname);
var debut   = missionTable.getCell(row,COL_DateDebut);
var fin     = missionTable.getCell(row,COL_DateFin);
//meibo.showField("Participant_box",true);

meibo.showField("OBJ_descriptionParticipants",true);
//meibo.showField("OBJ_participant",true);
meibo.getField("OBJ_participant").setValue("Mission de "+name+" "+surname+" du "+debut+" au "+fin);
meibo.showField("OBJ_listeEvents",true);

// On récupère la liste des évènements de la mission et on les affiche dans la table des évènements
var /*resultSet*/ missionEventRS = meibo.executeLink("MissionEvent", missionDn,"fkTCT_ID,TET_ID,TET_NAME,TCT_START_DATE,TCT_DURATION");
var /*field*/     eventTable = meibo.getField("OBJ_listeEvents");
var /*int*/       nbEvents = eventTable.fillInWithResultSet(missionEventRS,0,0,"TET_ID,TCT_START_DATE,TCT_DURATION,TET_NAME");


for (var row = 0; row < nbEvents; row++) {
  // On récupère la liste des participants et on lea met en Info bulle
  var /*string*/ bubbleInfo = "Participants à l'évènement "+eventTable.getCell(row,3)+"\n";
  var /*resultSet*/ eventAttendeesRS = meibo.executeLink("EventAttendees", "travel_event,fkTCT_ID,"+eventTable.getCell(row,0),"personDisplayName,RAS_START_DATE,RAS_END_DATE");
  for each(var eventAttendee in eventAttendeesRS){
    bubbleInfo = bubbleInfo + eventAttendee.personDisplayName[0]+" du "+eventAttendee.RAS_START_DATE[0]+" au " +eventAttendee.RAS_END_DATE[0]+"\n";
  }

  // L'insertion de l'info bulle est invalidée car elle pose problème 
  //dans le script OnClick de la table des events pour afficher le nom de l'évènement
  //eventTable.addRawCell(row,3,"<span title=\""+bubbleInfo+"\">" + eventTable.getCell(row,3) + "</span>");
}

